(function($){"use strict";$(window).on("elementor/frontend/init",function(){elementorFrontend.hooks.addAction("frontend/element_ready/vertical-slider-services.default",function($scope){var $slider=$scope.find(".vertical-slider-services.splide");if($slider.length===0)return;var sliderEl=$slider[0];if(sliderEl.splide){sliderEl.splide.destroy();sliderEl.classList.remove("is-initialized")}var optionsAttr=sliderEl.getAttribute("data-splide-options");var options={direction:"ttb",height:"500px",wheel:true,type:"loop",speed:400,gap:"10px"};if(optionsAttr){try{var customOptions=JSON.parse(optionsAttr);options=Object.assign({},options,customOptions)}catch(e){console.warn("Vertical Slider Services Editor: Invalid options JSON",e)}}var splide=new Splide(sliderEl,options);splide.mount();sliderEl.classList.add("is-initialized");sliderEl.splide=splide})})})(jQuery);
