(function(){"use strict";function initVerticalSliderServices(){var sliders=document.querySelectorAll(".vertical-slider-services.splide:not(.is-initialized)");sliders.forEach(function(slider){var optionsAttr=slider.getAttribute("data-splide-options");var options={direction:"ttb",height:"500px",wheel:true,type:"loop",speed:400,gap:"10px"};if(optionsAttr){try{var customOptions=JSON.parse(optionsAttr);options=Object.assign({},options,customOptions)}catch(e){console.warn("Vertical Slider Services: Invalid options JSON",e)}}var splide=new Splide(slider,options);splide.mount();slider.classList.add("is-initialized")})}if(document.readyState==="loading"){document.addEventListener("DOMContentLoaded",initVerticalSliderServices)}else{initVerticalSliderServices()}window.initVerticalSliderServices=initVerticalSliderServices})();
